var cover1_state = self.initNode("sim/model/bluebird/components/engine-cover1", 1, "BOOL");
var cover2_state = self.initNode("sim/model/bluebird/components/engine-cover2", 1, "BOOL");
var cover3_state = self.initNode("sim/model/bluebird/components/engine-cover3", 1, "BOOL");
var cover4_state = self.initNode("sim/model/bluebird/components/engine-cover4", 1, "BOOL");
var chair_state = self.initNode("sim/model/bluebird/crew/pilot/chair-back", 1, "BOOL");
var cg_state = self.initNode("controls/engines/countergrav-factor", 1, "BOOL");
var lights_interior_state = self.initNode("sim/model/bluebird/lighting/interior-switch", 1, "BOOL");
var lights_alert_state = self.initNode("controls/lighting/alert", 0, "BOOL");
var lights_overhead_red = self.getNode("sim/model/bluebird/lighting/overhead/emission/red");
var lights_overhead_gb = self.getNode("sim/model/bluebird/lighting/overhead/emission/gb");
var power_state_path = self.getNode("sim/multiplay/generic/int[2]").getPath();

# Detail bits in byte not visible in AI low-poly and older fg
var byte_state = self.getNode("sim/multiplay/generic/int[13]").getPath();
setlistener(byte_state, func (node) {
	var state = node.getValue();
	if (state != nil) {
		cover1_state.setBoolValue(bits.test(state, 0));
		cover2_state.setBoolValue(bits.test(state, 1));
		cover3_state.setBoolValue(bits.test(state, 2));
		cover4_state.setBoolValue(bits.test(state, 3));
		chair_state.setBoolValue(bits.test(state, 4));
		cg_state.setBoolValue(bits.test(state, 5));
		lights_interior_state.setBoolValue(bits.test(state, 6));
		lights_alert_state.setBoolValue(bits.test(state, 7));
		if (bits.test(state, 4)) {
			self.getNode("sim/model/bluebird/crew/pilot/visible").setValue(0);
		} else {
			self.getNode("sim/model/bluebird/crew/pilot/visible").setValue(1);
		}
		lights_overhead_red.setValue(bits.test(state, 6) * self.getNode("sim/multiplay/generic/int[2]").getValue());
		if (bits.test(state, 7)) {
			lights_overhead_gb.setValue(0);
		} else {
			lights_overhead_gb.setValue(bits.test(state, 6) * self.getNode("sim/multiplay/generic/int[2]").getValue());
		}
	} else {
		print (" bluebird-osg on multiplay sent invalid bits.");
	}
}, 1, 0);

var lights_door_safety_rgb_node = self.getNode("sim/model/bluebird/lighting/interiorB-door-lights/emission/rgb");
