setlistener(power_state_path, func (node) {
	power_state = node.getValue();
	lights_door_safety_rgb_node.setValue(power_state);
	self.getNode("sim/model/bluebird/lighting/interiorC-safety-lights/emission/rg").setValue(power_state);
	self.getNode("sim/model/bluebird/lighting/power-glow").setValue(power_state);
	lights_overhead_red.setValue(power_state *lights_interior_state.getValue());
	if (lights_alert_state.getValue()) {
		lights_overhead_gb.setValue(0);
	} else {
		lights_overhead_gb.setValue(power_state *lights_interior_state.getValue());
	}
	self.getNode("sim/model/bluebird/lighting/buttons/left1-power/face/emission/green").setValue(power_state);
}, 1, 0);

